$(document).ready(function() {
    $('#country').on('change', function() {
        var country = $(this).val();
        var uid = document.getElementById("uid").value;
        $.ajax({
        url: '/onboarding',
        type: 'POST',
        data: {
            country: country,
            csrf_token: odoo.csrf_token,
            uid:uid
        },
        success: function(data) {
            var states = JSON.parse(data).states;
            var stateSelect = $('#state');
            stateSelect.empty();
            $.each(states, function(index, state) {
            var option = $('').attr('value', state.id).text(state.name);
            stateSelect.append(option);
            });
        }

        });
    });


    $('#precountry').on('change', function() {
        var precountry = $(this).val();
        var uid = document.getElementById("uid").value;
        $.ajax({
        url: '/onboarding',
        type: 'POST',
        data: {
            precountry: precountry,
            csrf_token: odoo.csrf_token,
            uid:uid
        },
        success: function(data) {
            var states = JSON.parse(data).states;
            var stateSelect = $('#prestate');
            stateSelect.empty();
            $.each(states, function(index, state) {
            var option = $('').attr('value', state.id).text(state.name);
            stateSelect.append(option);
            });
        }

        });
    });
});
$(document).ready(function() {
    $('#street1').on('change', function() {
    if (!$(this).val() == ''){
        var streetInput = document.getElementById("street1")
        streetInput.setAttribute("style","");
        var streetError = document.getElementById("streetError")
        streetError.style.display = "none";
        streetError.textContent = "";
    }
});



    $('#city').on('change', function() {
    if (!$(this).val() == ''){
        var cityError = document.getElementById("cityError")
        var cityInput = document.getElementById("city");
        cityInput.setAttribute("style","");
        cityError.style.display = "none";
        cityError.textContent = "";
    }
});

    $('#state').on('change', function() {
    if (!$(this).val() == ''){
        var stateError = document.getElementById("stateError")
        var stateInput = document.getElementById("state");
        stateError.textContent = "";
        stateError.style.display = "none";
        stateInput.setAttribute("style","width: 0%;");
    }
});

    $('#postal').on('change', function() {
    if (!$(this).val() == ''){
        var postalError = document.getElementById("postalError")
        var postalInput = document.getElementById("postal");
        postalError.textContent = "";
        postalError.style.display = "none";
        postalInput.setAttribute("style","");
    }
});

    $('#country').on('change', function() {
    if (!$(this).val() == ''){
        var countryError = document.getElementById("countryError")
        var countryInput = document.getElementById("country");
        countryError.textContent = "";
        countryError.style.display = "none";
        countryInput.setAttribute("style","width: 0%;");

    }
});

    $('#email').on('change', function() {
    if (!$(this).val() == ''){
        var emailError = document.getElementById("emailError")
        var emailInput = document.getElementById("email");
        emailError.textContent = "";
        emailError.style.display = "none";
        emailInput.setAttribute("style","");
    }
});

$('#emergency_no_name').on('change', function() {
    if (!$(this).val() == ''){
        var emergency_no_nameError = document.getElementById("emergency_no_nameError")
        var emergency_no_nameInput = document.getElementById("emergency_no_name");
        emergency_no_nameError.textContent = "";
        emergency_no_nameError.style.display = "none";
        emergency_no_nameInput.setAttribute("style","");
    }
});

    $('#birthday').on('change', function() {
        inputBirthDate = $(this).val()
        const birthDate = new Date(inputBirthDate);
        const cutoffDate = new Date();
        cutoffDate.setFullYear(cutoffDate.getFullYear() - 18);
        var birthdayError = document.getElementById("birthdayError")
        var birthdayInput = document.getElementById("birthday");


        if (birthDate > cutoffDate){
            birthdayError.textContent = "Age must be 18+";
            birthdayError.style.display = "block";
            birthdayInput.setAttribute("style","box-shadow:0 0 8px tomato;border-color:tomato;width: 318px;");
        }else{
            birthdayError.textContent = "";
            birthdayError.style.display = "none";
            birthdayInput.setAttribute("style","width: 318px;");
        }

});

    $('#gender').on('change', function() {
    if (!$(this).val() == ''){
        var genderError = document.getElementById("genderError")
        genderError.textContent = "";
        genderError.style.display = "none";

    }
});


});

function deleteRecord(id){
    var confirmMessage = confirm("Are you sure to delete?");

    if (confirmMessage){
        var uid = document.getElementById("uid").value;
        if (id.match("education")){
            var deleteRecordIdObj = id.match(/\d+/)
            var deleteRecordId = parseInt(deleteRecordIdObj[0])

            var divId = document.getElementById('education_div'+ deleteRecordId)
            var recordId = document.getElementById('eduction_degree_id'+ deleteRecordId).value;

            var model = 'education'
            divId.remove()    
        }
        else if (id.match("experience_delete")){
            var deleteRecordIdObj = id.match(/\d+/)
            var deleteRecordId = parseInt(deleteRecordIdObj[0])

            var divId = document.getElementById('experience_div'+ deleteRecordId)
            var divId2 = document.getElementById('container_experience'+ deleteRecordId +'_previous_doc_div1')
            var hr1 =  document.getElementById('experience_hr_'+ deleteRecordId)
            if (document.getElementById(deleteRecordId + '_experience_hr_' + deleteRecordId)){ 
                var h2 = document.getElementById(deleteRecordId + '_experience_hr_' + deleteRecordId)
                h2.remove()
            }
            var recordId = document.getElementById('exp_company_id'+ deleteRecordId).value;


            var experience_message = document.getElementById('error_experience_previous_document'+deleteRecordId)

            var model = 'experience'
            divId.remove()
            divId2.remove()
            hr1.remove()
            experience_message.remove()

        }
        else if (id.match("previous_delete")){
            var deleteRecordIdObj = id.match(/\d+/g).map(Number)


            var divId = document.getElementById('experience' + deleteRecordIdObj[0] + '_previous_div'+ deleteRecordIdObj[1])
            var recordId = document.getElementById('experience' +deleteRecordIdObj[0] + '_previous_emp_doc_type_id'+deleteRecordIdObj[1]).value;


            var model = 'previous'
            divId.remove()

        }
        else if (id.match("identity")){
            var deleteRecordIdObj = id.match(/\d+/)
            var deleteRecordId = parseInt(deleteRecordIdObj[0])

            var divId = document.getElementById('identity_div'+ deleteRecordId)
            var recordId = document.getElementById('identity_type_id'+ deleteRecordId).value;


            var model = 'identity'
            divId.remove()

        }
        if (recordId){
            $.ajax({
                url: '/onboarding',
                type: 'POST',
                data: {
                    uid:uid,
                    recordId:recordId,
                    csrf_token: odoo.csrf_token,
                    model:model,
                },
                success: function(data) {
                    console.log("success")
                }
            });
        }

    }
}

var counter = 1;
var currentSection = 1;
var numSection = 3;

function previousPage(record_previous_id) {



    if (currentSection > 1) {
            currentSection--;
            showSection(currentSection);
        }

}



function validateMobileNumber(event) {
    var charCode = event.which ? event.which : event.keyCode;
    var mobile_noInput = document.getElementById("mobile_no");
    var mobile_noError = document.getElementById("mobile_noError")
    var mobile_no = document.getElementById("mobile_no").value;
    if ((charCode > 47 && charCode < 58)) {
        if ((mobile_no.length + 1) < 10){
            mobile_noError.textContent = "Please enter valid Mobile No";
            mobile_noError.style.display = "block";
            mobile_noInput.setAttribute("style","box-shadow:0 0 8px tomato;border-color:tomato;");
            return true;
        }else{
            mobile_noError.textContent = "";
            mobile_noError.style.display = "none";
            mobile_noInput.setAttribute("style","");
            return true;
        }

    } else if(charCode == 8){
        if ((mobile_no.length - 1) < 10){
            mobile_noError.textContent = "Please enter valid Mobile No";
            mobile_noError.style.display = "block";
            mobile_noInput.setAttribute("style","box-shadow:0 0 8px tomato;border-color:tomato;");
            return true;
        }else{
            mobile_noError.textContent = "";
            mobile_noError.style.display = "none";
            mobile_noInput.setAttribute("style","");
            return true;
        }
    }
    else {
        return false;
    }
}



function validatePinCodeNumber(event) {
    var charCode = event.which ? event.which : event.keyCode;
    var postalInput = document.getElementById("postal");
    var postalError = document.getElementById("postalError")
    var postal = document.getElementById("postal").value;

    if ((charCode > 47 && charCode < 58)) {
        if ((postal.length + 1) < 6){
            postalError.textContent = "Please enter valid postal code";
            postalError.style.display = "block";
            postalInput.setAttribute("style","box-shadow:0 0 8px tomato;border-color:tomato;");
            return true;
        }else{
            postalError.textContent = "";
            postalError.style.display = "none";
            postalInput.setAttribute("style","");
            return true;
        }

    } else if(charCode == 8){
        if ((postal.length - 1) < 6){
            postalError.textContent = "Please enter valid postal code";
            postalError.style.display = "block";
            postalInput.setAttribute("style","box-shadow:0 0 8px tomato;border-color:tomato;");
            return true;
        }else{
            postalError.textContent = "";
            postalError.style.display = "none";
            postalInput.setAttribute("style","");
            return true;
        }
    }
    else {
        return false;
    }
}





function autoFilPresentAddress(){
    var checkBox= document.getElementById('samePresentPermanent');
    if (checkBox.checked == true){
        document.getElementById('prestreet1').value = document.getElementById('street1').value;
        document.getElementById('prelandmark').value = document.getElementById('landmark').value;
        document.getElementById('precity').value = document.getElementById('city').value;
        document.getElementById('prepostal').value = document.getElementById('postal').value;
        if(document.getElementById('country').value){
            var precountry = document.getElementById('country').value;
            var uid = document.getElementById("uid").value;

            $.ajax({
            url: '/onboarding',
            type: 'POST',
            data: {
                precountry: precountry,
                prestate:document.getElementById('state').value,
                csrf_token: odoo.csrf_token,
                uid:uid
            },
            success: function(data) {
                var states = JSON.parse(data).states;
                var prestate = JSON.parse(data).prestate;
                var stateSelect = $('#prestate');
                stateSelect.empty();

                if (prestate){
                    $.each(prestate, function(index, state) {
                    var option = $('').attr('value', state.id).text(state.name);
                    stateSelect.append(option);
                    });
                }
                $.each(states, function(index, state) {
                var option = $('').attr('value', state.id).text(state.name);
                stateSelect.append(option);
                });
            }

            });
            document.getElementById('precountry').value = document.getElementById('country').value;
            document.getElementById('prestate').value = document.getElementById('state').value; 
        }  


    }else{
        document.getElementById('prestreet1').value = '';
        document.getElementById('prelandmark').value = '';
        document.getElementById('precity').value = '';
        document.getElementById('precountry').value = '';
        document.getElementById('prestate').value = '';
        document.getElementById('prepostal').value = '';

    }
}



function validatePresentPinCodeNumber(event) {
    var charCode = event.which ? event.which : event.keyCode;
    var postalInput = document.getElementById("prepostal");
    var postalError = document.getElementById("prepostalError")
    var prepostal = document.getElementById("prepostal").value;


    if ((charCode > 47 && charCode < 58)) {
        if ((prepostal.length + 1) < 6){
            postalError.textContent = "Please enter valid postal code";
            postalError.style.display = "block";
            postalInput.setAttribute("style","box-shadow:0 0 8px tomato;border-color:tomato;");
            return true;
        }else{
            postalError.textContent = "";
            postalError.style.display = "none";
            postalInput.setAttribute("style","");
            return true;
        }

    } else if(charCode == 8){
        if ((prepostal.length - 1) < 6){
            postalError.textContent = "Please enter valid postal code";
            postalError.style.display = "block";
            postalInput.setAttribute("style","box-shadow:0 0 8px tomato;border-color:tomato;");
            return true;
        }else{
            postalError.textContent = "";
            postalError.style.display = "none";
            postalInput.setAttribute("style","");
            return true;
        }
    }
    else {
        return false;
    }
}




