function findCloseDate(targetDate,experienceHierarchy){
    var closestDate;
    var closestDiff = Infinity;

    for (var key in experienceHierarchy) {
        const value = experienceHierarchy[key];

        var currentDate = new Date(value);

        var diff = Math.abs(targetDate - currentDate);
        if (diff < closestDiff) {
            closestDiff = diff;
            closestDate = currentDate;
        }
    }

    return closestDate;

}

function formValidationpreExperience(){

    var uid = document.getElementById("uid").value;
    experienceHierarchy = {}
    exptag = 1
    while (document.getElementById('exp_company'+exptag)){
        if(document.getElementById('exp_end_date'+exptag).value){
            experienceHierarchy['exp_end_date'+exptag] = document.getElementById('exp_end_date'+exptag).value
        }

        exptag += 1
    }



    var currentDate = new Date();
    var closeDate = findCloseDate(currentDate,experienceHierarchy)


    focustInput = []
    flag = 0


    var experienceTagFlag = 1

    while (document.getElementById('exp_company'+experienceTagFlag)){

        if(!(document.getElementById('exp_company'+experienceTagFlag).value)){
            var error_experience = document.getElementById('error_exp_company'+experienceTagFlag)
            var error_input = document.getElementById('exp_company'+experienceTagFlag)
            error_experience.textContent = "Please enter company name";
            if (focustInput == ""){ focustInput.push(error_input) }
            flag = 1
        }else{

            var error_experience = document.getElementById('error_exp_company'+experienceTagFlag)
            error_experience.textContent = ""; 
        }

        if(!(document.getElementById('exp_start_date'+experienceTagFlag).value)){
            var error_experience = document.getElementById('error_exp_start_date'+experienceTagFlag)
            var error_input = document.getElementById('exp_start_date'+experienceTagFlag)
            error_experience.textContent = "Please enter start date";
            if (focustInput == ""){ focustInput.push(error_input) }
            flag = 1

        }else{

            var error_experience = document.getElementById('error_exp_start_date'+experienceTagFlag)
            error_experience.textContent = "";
        }

        if(!(document.getElementById('exp_end_date'+experienceTagFlag).value)){
            var error_experience = document.getElementById('error_exp_end_date'+experienceTagFlag)
            var error_input = document.getElementById('exp_end_date'+experienceTagFlag)
            error_experience.textContent = "Please enter end date";
            if (focustInput == ""){ focustInput.push(error_input) }
            flag = 1
        }else{

            var error_experience = document.getElementById('error_exp_end_date'+experienceTagFlag)
            error_experience.textContent = "";
        }





        var preDocumentTagFlag = 1
        var document_dictionary = {}
        while (document.getElementById('experience'+ experienceTagFlag +'_previous_emp_doc_type'+preDocumentTagFlag)){
            if (!(document.getElementById('experience'+ experienceTagFlag +'_previous_emp_doc_type'+preDocumentTagFlag).value)){
                var error_eduction = document.getElementById('experience'+ experienceTagFlag +'_error_previous_emp_doc_type'+preDocumentTagFlag)
                var error_input = document.getElementById('experience'+ experienceTagFlag +'_previous_emp_doc_type'+preDocumentTagFlag)
                error_eduction.textContent = "Select Document";
                if (focustInput == ""){ focustInput.push(error_input) }
                flag = 1
            }else{
                document_dictionary['experience'+ experienceTagFlag +'_previous_emp_doc_type'+preDocumentTagFlag] = document.getElementById('experience'+ experienceTagFlag +'_previous_emp_doc_type'+preDocumentTagFlag).value
                var error_eduction = document.getElementById('experience'+ experienceTagFlag +'_error_previous_emp_doc_type'+preDocumentTagFlag)
                error_eduction.textContent = "";
            }

            if(((document.getElementById('experience'+ experienceTagFlag +'_pervious_emp_doc_attachment'+preDocumentTagFlag).files.length) == 0) && (!(document.getElementById('experience'+ experienceTagFlag +'_previous_emp_doc_type_id'+preDocumentTagFlag).value))){
                var error_input_div = document.getElementById('experience'+ experienceTagFlag +'_file_pervious_emp_doc_attachment'+preDocumentTagFlag)
                error_input_div.setAttribute("style","box-shadow:0 0 8px tomato;border-color:tomato;");
                if (focustInput == ""){ focustInput.push(error_input_div) }
                flag = 1
            }else {
                if ($('#experience'+ experienceTagFlag +'_pervious_emp_doc_attachment'+preDocumentTagFlag)[0].files[0]){
                    var file = $('#experience'+ experienceTagFlag + '_pervious_emp_doc_attachment'+preDocumentTagFlag)[0].files[0]
                    const fileExtension = file.name.split('.').pop().toLowerCase(); // Get the file extension
                    const allowedExtensions = ['jpg', 'jpeg', 'png', 'gif', 'pdf'];

                    if (!allowedExtensions.includes(fileExtension)) {
                        var error_input_div = document.getElementById('experience'+ experienceTagFlag +'_file_pervious_emp_doc_attachment'+preDocumentTagFlag)
                        error_input_div.setAttribute("style","box-shadow:0 0 8px tomato;border-color:tomato;");
                        if (focustInput == ""){ focustInput.push(error_input_div) }
                        flag = 1


                        var div_tag_main = document.getElementById("error_experience_previous_document"+ experienceTagFlag)
                        var div_tag = document.getElementById("label_error_experience"+ experienceTagFlag +"_previous_document" + preDocumentTagFlag)
                        if (div_tag){
                            var div_label = div_tag
                        }else{
                            var div_label = document.createElement('label');
                        }

                        div_label.setAttribute('name','label_error_experience'+ experienceTagFlag +'_previous_document'+preDocumentTagFlag);
                        div_label.setAttribute('id','label_error_experience'+ experienceTagFlag +'_previous_document'+preDocumentTagFlag);
                        div_label.setAttribute("style","color:tomato;font-size:12px;margin-bottom: 0px;");
                        div_label.textContent = 'Please select a valid image (JPEG, PNG) or PDF file for '  + document.getElementById('experience'+ experienceTagFlag +'_previous_emp_doc_type'+preDocumentTagFlag).value + '.';
                        div_tag_main.appendChild(div_label)



                    }
                    else {

                        var div_tag = document.getElementById("label_error_experience"+ experienceTagFlag +"_previous_document" + preDocumentTagFlag)
                        if (div_tag){
                           div_tag.remove() 
                        }


                        var error_input_div = document.getElementById('experience'+ experienceTagFlag +'_file_pervious_emp_doc_attachment'+preDocumentTagFlag)
                        error_input_div.setAttribute("style","box-shadow:0 0 8px green;border-color:green;");
                    }
                }

            }

            preDocumentTagFlag += 1

        }
        const result = [];
        const seen = [];
        mandatory_previous_document = []
        for (const key in document_dictionary) {
            const value = document_dictionary[key];

            if (seen[value]) {
                result.push(key);
            } else {
                seen[value] = true;
                mandatory_previous_document.push(value)
            }
        }
        for (let i = 0; i < result.length; i++) {  
            var error_identity = document.getElementById('experience' + experienceTagFlag +'_error_previous_emp_doc_type' + result[i].match(/\d+/g).map(Number)[1])
            var error_input = document.getElementById(result[i])
            error_identity.textContent = "Document already exist";
            if (focustInput == ""){ focustInput.push(error_input) }
            flag = 1  
        }

        if((document.getElementById('exp_end_date'+experienceTagFlag).value)){

            exp_date_current = new Date(document.getElementById('exp_end_date'+experienceTagFlag).value)
            if (closeDate.getTime() === exp_date_current.getTime()){
                if ((!(mandatory_previous_document.includes('paySlip'))) || (!(mandatory_previous_document.includes('appointment')))){
                    var error_msg_for_mandatory_type = document.getElementById('label_error_experience_previous_document'+experienceTagFlag)

                    error_msg_for_mandatory_type.textContent = "Payslip and Appointment are mandatory Document of last company.";
                    if (focustInput == ""){ focustInput.push(error_msg_for_mandatory_type) }
                    flag = 1 
                }else{
                    var error_msg_for_mandatory_type = document.getElementById('label_error_experience_previous_document'+experienceTagFlag)

                    error_msg_for_mandatory_type.textContent = "";
                }


            }else{
                if ((!(mandatory_previous_document.includes('relieving'))) || (!(mandatory_previous_document.includes('appointment'))) ){
                    var error_msg_for_mandatory_type = document.getElementById('label_error_experience_previous_document'+experienceTagFlag)
                    error_msg_for_mandatory_type.textContent = "Appointment and Relieving letter are mandatory Document of previous company.";
                    if (focustInput == ""){ focustInput.push(error_msg_for_mandatory_type) }
                    flag = 1 
                }else{
                    var error_msg_for_mandatory_type = document.getElementById('label_error_experience_previous_document'+experienceTagFlag)
                    error_msg_for_mandatory_type.textContent = "";
                }
            }
        }

        experienceTagFlag += 1
    }


    if (flag == 1){
        focustInput[0].focus()
        return false
    }else{
        var spinner = $('#loader');
        spinner.show()

    }

}



function nextPage(record_next_id) {


    if (record_next_id == "presonalInfoOnboarding"){

        var uid = document.getElementById("uid").value;
        var first_name = document.getElementById("first_name").value;
        var last_name = document.getElementById("last_name").value;
        var street1 = document.getElementById("street1").value;
        var landmark = document.getElementById("landmark").value;
        var city = document.getElementById("city").value;
        var country = document.getElementById("country").value;
        var state = document.getElementById("state").value;
        var postal = document.getElementById("postal").value;
        var blood_group = document.getElementById("blood_group").value;

        var prestreet1 = document.getElementById("prestreet1").value;
        var prelandmark = document.getElementById("prelandmark").value;
        var precity = document.getElementById("precity").value;
        var precountry = document.getElementById("precountry").value;
        var prestate = document.getElementById("prestate").value;
        var prepostal = document.getElementById("prepostal").value;

        var mobile_no = document.getElementById("mobile_no").value;
        var email = document.getElementById("email").value;
        var emergency_no_name = document.getElementById("emergency_no_name").value;
        var emergency_no = document.getElementById("emergency_no").value;
        var birthday = document.getElementById("birthday").value;
        var gender = document.querySelector('input[name="gender"]:checked')
        var isPresentSameAsPermanent = document.getElementById("samePresentPermanent").checked

        var focustInput = []
        flag = 0

        var firstNameError = document.getElementById("firstNameError")
        var firstNameInput = document.getElementById("first_name");
        var regName = /^[a-zA-Z]+$/;
        if (first_name == "") {
            firstNameError.textContent = "Please enter first name";
            firstNameError.style.display = "block";
            firstNameInput.setAttribute("style","box-shadow:0 0 8px tomato;border-color:tomato");
            if (focustInput == ""){ focustInput.push(firstNameInput) }
            flag= 1
        }else if(!regName.test(first_name)){
            firstNameError.textContent = "Please enter valid first name";
            firstNameError.style.display = "block";
            firstNameInput.setAttribute("style","box-shadow:0 0 8px tomato;border-color:tomato");
            if (focustInput == ""){ focustInput.push(firstNameInput) }
            flag= 1
        }else{
            firstNameError.style.display = "none";
            firstNameInput.setAttribute("style","");
        }


        var secondNameError = document.getElementById("secondNameError")
        var secondNameInput = document.getElementById("last_name");
        if (last_name == "") {
            secondNameError.textContent = "Please enter last name";
            secondNameError.style.display = "block";
            secondNameInput.setAttribute("style","box-shadow:0 0 8px tomato;border-color:tomato");
            if (focustInput == ""){ focustInput.push(secondNameInput) }
            flag= 1
