                    console.log("total == ",documentData.hasOwnProperty('experience'+ expFlag + '_previous_emp_doc_type' + preEmpFlag))
                    if (preEmpFlag <= 3){
                        if (!(document.getElementById('experience'+ expFlag + '_previous_emp_doc_type' + preEmpFlag))){
                            var vals = 'container_experience' +expFlag+ '_previous_doc_div1'
                            add_more_previous_document(vals)
                        }
                        var previous_emp_doc = document.getElementById('experience'+ expFlag + '_previous_emp_doc_type' + preEmpFlag)
                        previous_emp_doc.value = documentData['experience'+ expFlag +'_previous_emp_doc_type'+preEmpFlag]

                        var previous_emp_doc_attachment_data = document.getElementById('experience'+ expFlag + '_file_pervious_emp_doc_attachment'+ preEmpFlag)
                        previous_emp_doc_attachment_data.setAttribute("style","box-shadow:0 0 8px green;border-color:green;");

                        var div_tag_input_pre_doc_id = document.getElementById('experience'+ expFlag +'_previous_emp_doc_type_id'+preEmpFlag)
                        div_tag_input_pre_doc_id.value = documentData['experience'+ expFlag +'_previous_emp_doc_type_id'+preEmpFlag]

                    }
                    else   
                    {
                        if (elements.length != previous_emp_doc_type_count.length){
                            var vals = 'container_experience' +expFlag+ '_previous_doc_div1'
                            add_more_previous_document(vals,documentData)
                        }

                    }
                   preEmpFlag += 1 
                }  
                expFlag += 1
            }

        }
        });
        if (currentSection < numSection) {
            currentSection++;
            showSection(currentSection);
        }
    }


}

function showSection(sectionNumber) {

    for (let i = 1; i <= numSection; i++) {
        if (i === sectionNumber) {
        document.getElementById(`page_${i}`).style.display = "block";
        } else {
        var test = document.getElementById(`page_${i}`)
        document.getElementById(`page_${i}`).style.display = "none";
        }
    }
}

