function add_more_previous_document(val,documentData = 1){
   company_number = val.match(/\d+/g);
   var counter_objcet = 'experience' + company_number[0] + '_pervious_emp_doc_attachment'
   countflag = document.querySelectorAll("[id*='" + counter_objcet + "']");

   counter = countflag.length + 1

   var div_tag = document.getElementById(val)
   var div_tag_create = document.createElement('div');
   div_tag_create.setAttribute('id','experience' + company_number[0] +'_previous_div'+counter);
   div_tag_create.classList.add("row")


   var div_tag_select = document.createElement('div');
   div_tag_select.classList.add("col-1","form-input-wide");
   div_tag_select.setAttribute("style","margin-top:10px;padding-right: 0px;");

   var div_inside_tag = document.createElement('div');
   div_inside_tag.setAttribute("style","height: 40px;width:12px;background-color:lightgrey;border-radius: 4px;");


   div_tag_select.appendChild(div_inside_tag)
   div_tag_create.appendChild(div_tag_select)





   var div_tag_select = document.createElement('div');
   div_tag_select.classList.add("col-5","form-input-wide");
   div_tag_select.setAttribute("style","margin:10px 0 0 -10px;padding:0px");

   var div_select = document.createElement('select');
   div_select.classList.add('form-dropdown');
   div_select.setAttribute('name','experience' + company_number[0] + '_previous_emp_doc_type'+counter);
   div_select.setAttribute('id','experience' + company_number[0] + '_previous_emp_doc_type'+counter);
   div_select.setAttribute('style','font-family: Source Sans Pro;opacity: 75%');

   var dict_options = {
        '':'Document Type',
        'appointment':'Appointment',
        'appraisal':'Appraisal',
        'confirmation':'Confirmation',
        'nomination':'Nomination',
        'exitForm':'Exit Form',
        'experience':'Experience',
        'offer':'Offer',
        'paySlip':'Pay Slip',
        'relieving':'Relieving',
        'other':'Other',      
    }

   for (var key in dict_options){
        if (dict_options.hasOwnProperty(key)) {
            var value = dict_options[key];
            var option = document.createElement("option");
            option.value = key;
            option.text = value;
            div_select.appendChild(option);
        }
   }
   if (!(documentData == 1)){
        div_select.value = documentData['experience' + company_number[0]+'_previous_emp_doc_type'+preEmpFlag]
   }
   div_tag_select.appendChild(div_select)

   var div_input = document.createElement('input');
   div_input.setAttribute('name','experience' + company_number[0] + '_previous_emp_doc_type_id'+counter);
   div_input.setAttribute('id','experience' + company_number[0] + '_previous_emp_doc_type_id'+counter);
   div_input.setAttribute('type','hidden');

   if (!(documentData == 1)){
        div_input.value = documentData['experience' + company_number[0]+'_previous_emp_doc_type_id'+preEmpFlag]
   }

   div_tag_select.appendChild(div_input)

   var div_input = document.createElement('label');
   div_input.setAttribute('id','experience' + company_number[0] + '_error_previous_emp_doc_type'+counter);
   div_input.setAttribute('style','font-size: 14px;color: tomato;margin:0px');
   div_input.className = 'form-sub-label';
   div_tag_select.appendChild(div_input)

   div_tag_create.appendChild(div_tag_select)



   var div_tag_input = document.createElement('div');
   div_tag_input.classList.add("col-3","form-input-wide");
   div_tag_input.setAttribute("style","margin-top:10px;padding-right: 0px;");


   var div_input = document.createElement('input');
   div_input.setAttribute('name','experience' + company_number[0] + '_pervious_emp_doc_attachment'+counter);
   div_input.setAttribute('id','experience' + company_number[0] + '_pervious_emp_doc_attachment'+counter);
   div_input.setAttribute('type','file');
   div_input.setAttribute('style','display:none;');


   var div_tag_label = document.createElement('div');
   div_tag_label.classList.add('form-textbox');
   div_tag_label.setAttribute('id','experience' + company_number[0] + '_file_pervious_emp_doc_attachment'+counter);

   if (!(documentData == 1)){
       div_tag_label.setAttribute("style","box-shadow:0 0 8px green;border-color:green;");
   } 
   div_tag_input.appendChild(div_tag_label)
   div_tag_create.appendChild(div_tag_input)

   var div_label = document.createElement('label');
   div_label.setAttribute('style','font-size: 14px; color: black; opacity: 75%;margin-left: 0px;margin-top: 9px;font-weight: 300');
   div_label.className = 'bi bi-paperclip';
   div_label.setAttribute('for','experience' + company_number[0] + '_pervious_emp_doc_attachment'+counter)
   div_label.textContent = ' Attach file'

   div_tag_label.appendChild(div_input)
   div_tag_label.appendChild(div_label)
   div_tag_input.appendChild(div_tag_label)



    var div_tag_button = document.createElement('div');
    div_tag_button.classList.add("col-1","form-input-wide");
    div_tag_button.setAttribute("style","margin-top:10px;");

    var div_button = document.createElement('button');
    div_button.setAttribute('name','experience' + company_number[0] + '_previous_delete'+counter);
    div_button.setAttribute('id','experience' + company_number[0] + '_previous_delete'+counter);
    div_button.setAttribute('style','border-radius: 5px;padding: 6px;width: 40px;');
    div_button.classList.add("bi","bi-trash","btn","btn-danger");
    div_button.setAttribute("onclick","deleteRecord(this.id)");  
    div_button.setAttribute("type","button")

    div_tag_button.appendChild(div_button)
    div_tag_create.appendChild(div_tag_button)




   var div_tag_button = document.createElement('div');
   div_tag_button.classList.add("col-2","form-input-wide");
   div_tag_button.setAttribute("style","margin-top:2px;padding-left: 0px");

   var div_button = document.createElement('button');
    div_button.setAttribute('name',val);
    div_button.setAttribute('id',val);
    div_button.setAttribute('style','width: 103px;margin-left: 12px;');
    div_button.classList.add("btn","btn-secondary","float-left","mt-2");
    div_button.setAttribute("onclick","add_more_previous_document(this.id)");  
    div_button.setAttribute("type","button")
    div_button.textContent = "Add more +"

    div_tag_button.appendChild(div_button)
    div_tag_create.appendChild(div_tag_button)

    div_tag.appendChild(div_tag_create) 

};


